class GameTimer{constructor(){this.gameDuration=0,this.isPaused=!1,this.timerInterval=null,this.gameTimerElement=document.getElementById("game-timer")}start(){this.timerInterval||(this.timerInterval=setInterval((()=>{this.updateGameTimer()}),1e3))}reset(){this.stop(),this.start()}pause(){this.isPaused=!0}resume(){this.isPaused=!1}stop(){this.timerInterval&&(clearInterval(this.timerInterval),this.timerInterval=null),this.gameDuration=0}updateGameTimer(){if(!this.isPaused){const t=Math.floor(this.gameDuration/60),e=this.gameDuration%60;this.gameTimerElement.textContent=`${t}:${e<10?"0":""}${e}`,this.gameDuration++}}}export default GameTimer;