class GuessedWords{constructor(s){this.wordsCount=s,this.guessedWordsCount=0,this.guessedWordsCounterElement=document.getElementById("guessed-words-counter"),this.updateGuessedWordsCounter()}increaseGuessedWordsCount(){this.guessedWordsCount++,this.updateGuessedWordsCounter()}resetCounter(){this.guessedWordsCount=0,this.updateGuessedWordsCounter()}updateGuessedWordsCounter(){this.guessedWordsCounterElement.textContent=`${this.guessedWordsCount} ли ${this.wordsCount}`}}export default GuessedWords;