class HintBox{constructor(t){this.gameTimer=t,this.hintBox=document.querySelector(".hint-box"),this.hintTextElement=document.querySelector(".hint-box__text"),this.hintTextDefault="Це підказка для кнопки.",this.addEventListeners()}toggleHint(){this.hintBox.classList.contains("hidden")?this.hintBox.classList.remove("hidden"):this.hintBox.classList.add("hidden")}addEventListeners(){window.addEventListener("click",(t=>{t.target===this.hintBox&&this.hideHint()})),window.addEventListener("keydown",(t=>{"Escape"===t.key&&this.hideHint()}))}showHint(t){this.gameTimer.pause(),this.hintTextElement.textContent=t??this.hintTextDefault,this.hintBox.classList.remove("hidden")}hideHint(){this.gameTimer.resume(),this.hintBox.classList.add("hidden"),this.hintTextElement.textContent=""}}export default HintBox;